# 这个程序是Zerg的AI分类# pip install -i https://pypi.tuna.tsinghua.edu.cn/simple xxxx# 打包命令 pyinstaller xxxxxxxx --onefile --icon=xxxxxxxxxximport jsonimport sysfrom main1 import *import time#加载json文件def load_json():    with open('E:/pythonxiangmu/sc2/resource/tongxin.json', 'r') as f:        data = json.load(f)#将json文件转换为字典，返回字典def json_to_dict():    with open('E:/pythonxiangmu/sc2/resource/tongxin.json', 'r') as f:        data = json.load(f)        return datadef baozhaweixie():    app = QApplication(sys.argv)    widget = MyWidget('双爆Z','爆炸威胁','隐身宿主！！')    widget.show()    widget.zhiding()    QTimer.singleShot(3500, widget.close)    app.exec_()    app.quit()def zishengfuhuan():    app = QApplication(sys.argv)    widget = MyWidget('空军Z','滋生腐化')    widget.show()    widget.zhiding()    QTimer.singleShot(3500, widget.close)    app.exec_()    app.quit()def zhetianbiri():    app = QApplication(sys.argv)    widget = MyWidget('经典空军Z','遮天蔽日')    widget.show()    widget.zhiding()    QTimer.singleShot(3500, widget.close)    app.exec_()    app.quit()def sinuekuosan():    app = QApplication(sys.argv)    widget = MyWidget('纯地面Z','肆虐扩散','隐身地刺!!','无空中单位!')    widget.show()    widget.zhiding()    QTimer.singleShot(3500, widget.close)    app.exec_()    app.quit()def qinluechongqun():    app = QApplication(sys.argv)    widget = MyWidget('经典地面Z','侵略虫群','隐身地刺!!')    widget.show()    widget.zhiding()    QTimer.singleShot(3500, widget.close)    app.exec_()    app.quit()def shibieAI_Z():    #判断字典中的键值对，"aibool15"-"aibool9"d的值是否为True，如果为True，则执行对应的函数    while True:        load_json()        aiandrace = json_to_dict()        if aiandrace["aibool15"] == True:            baozhaweixie()            break        elif aiandrace["aibool14"] == True:            zishengfuhuan()            break        elif aiandrace["aibool13"] == True:            zhetianbiri()            break        elif aiandrace["aibool12"] == True:            sinuekuosan()            break        elif aiandrace["aibool11"] == True:            qinluechongqun()            break        else:            break        time.sleep(1)shibieAI_Z()